PDG_slick_gallery.prototype.loadImagesAndInit = function() {
  var myobj = this;
  console.log('PDG_slick_gallery.prototype.loadImagesAndInit');

  var imgLoaded = 0;
  var imgAppended = 0;
  
  if (myobj.data["data"]["photos"].length == 0) {
    console.log("PDG_slick_gallery loading for 0 photos");
    myobj.container.find(".slickcontainer").slick({
      dots: false,
      arrows:true,
      infinite: true,
      speed: 800,
      slidesToShow: 1,
      centerMode: true,
      variableWidth: true,    
      slidesToScroll: 1,
      focusOnSelect: true,
      cssEase: "ease-out",
      autoplay: true,
      autoplaySpeed: 3000,
    });  
    myobj.resize( myobj.container.find(".slickcontainer") ) ;
  }
  else {
    console.log('PDG_slick_gallery loading ' + myobj.data["data"]["photos"].length + ' photos');
    for (var i=0; i<myobj.data["data"]["photos"].length; i++) {
      var ldiv = jQuery('<div/>', {
        class: 'datacontainer'
      });

      var limg = jQuery('<img/>', {
        // src: myobj.data["data"]["photos"][i]["image"]
        src: site_images[ myobj.data["data"]["photos"][i]["image-id"] ]["image"],
      });

      limg.one("load", function(i) {
        imgLoaded++;
        if ( (imgLoaded == imgAppended) && (imgLoaded == myobj.data["data"]["photos"].length) ) {
          myobj.container.find(".slickcontainer").slick({
            dots: false,
            arrows:true,
            infinite: true,
            speed: 800,
            slidesToShow: 1,
            centerMode: true,
            variableWidth: true,    
            slidesToScroll: 1,
            focusOnSelect: true,
            cssEase: "ease-out",
            autoplay: true,
            autoplaySpeed: 3000,
          });  
          
          myobj.resize( myobj.container.find(".slickcontainer") );
        }
      });

      ldiv.append(limg);

      myobj.container.find(".slickcontainer").append(ldiv);
      imgAppended++;
      if ( (imgLoaded == imgAppended) && (imgLoaded == myobj.data["data"]["photos"].length) ) {
        myobj.container.find(".slickcontainer").slick({
          dots: false,
          arrows:true,
          infinite: true,
          speed: 800,
          slidesToShow: 1,
          centerMode: true,
          variableWidth: true,    
          slidesToScroll: 1,
          focusOnSelect: true,
          cssEase: "ease-out",
          autoplay: true,
          autoplaySpeed: 3000,

        });

        myobj.resize( myobj.container.find(".slickcontainer") );
      }
    }
  }   
}


